mapper.update:
update sys_dept sd

     sd.parent_id = #{parentId}, 
     sd.name = #{name}, 
     sd.order_num = #{orderNum}, 
     sd.del_flag = #{delFlag} 

where sd.dept_id = #{deptId}

mapper.select:
select sd.dept_id, sd.parent_id, sd.name, sd.order_num, sd.del_flag from sys_dept sd
sd.dept_id = #{deptId}, sd.parent_id = #{parentId}, sd.name = #{name}, sd.order_num = #{orderNum}, sd.del_flag = #{delFlag}
order by sd.order_num
