mapper.update:
update sys_menu sm
sm.parent_id = #{parentId}, sm.name = #{name}, sm.url = #{url}, sm.perms = #{perms}, sm.type = #{type}, sm.icon = #{icon}, sm.order_num = #{orderNum}, sm.gmt_create = #{gmtCreate}, sm.gmt_modified = #{gmtModified}
where sm.menu_id = #{menuId}

mapper.select:
select sm.menu_id, sm.parent_id, sm.name,
       sm.url, sm.perms, sm.type, sm.icon,
       sm.order_num,sm.gmt_create,sm.gmt_modified
from sys_menu sm

    and menu_id = #{menuId}
    and parent_id = #{parentId}
    and name = #{name}
    and url = #{url}
    and perms = #{perms}
    and type = #{type}
    and icon = #{icon}
    and order_num = #{orderNum}
    and gmt_create = #{gmtCreate}
    and gmt_modified = #{gmtModified}
