mapper.select:
select sr.role_id, sr.role_name, sr.role_sign,
sr.remark, sr.user_id_create,
sr.gmt_create,sr.gmt_modified
from sys_role sr

    and sr.role_id = #{roleId}
    and sr.role_name = #{roleName}
    and sr.role_sign = #{roleSign}
    and sr.remark = #{remark}
    and sr.user_id_create = #{userIdCreate}
    and sr.gmt_create = #{gmtCreate}
    and sr.gmt_modified = #{gmtModified}

select count(1)
from sys_role sr

    and sr.role_id = #{roleId}
    and sr.role_name = #{roleName}
    and sr.role_sign = #{roleSign}
    and sr.remark = #{remark}
    and sr.user_id_create = #{userIdCreate}
    and sr.gmt_create = #{gmtCreate}
    and sr.gmt_modified = #{gmtModified}

mapper.update:
update sys_role sr
sr.role_name = #{roleName}, sr.role_sign = #{roleSign}, sr.remark = #{remark}, sr.user_id_create = #{userIdCreate}, sr.gmt_modified = #{gmtModified}
where sr.role_id = #{roleId}
